row.entry:
(
	(
		'@IsReceipt@' = 'Y' AND COF_C_ControlCheck.C_Payment_ID IS NULL AND COF_C_ControlCheck.C_BPartner_ID=@C_BPartner_ID@
	)
	OR
	(
		'@IsReceipt@' = 'N' AND COF_C_ControlCheck.COF_Payment_ID IS NULL AND (COF_C_ControlCheck.cof_UseForPayment = 'Y' OR COF_C_ControlCheck.cof_IsOwnCheck = 'Y')
	)
)
